import React from 'react'
import "./Search.css"
import { useSearchParams } from 'react-router-dom'
import useFetch from './../../hooks/useFetch';
import Loading from '../../components/Loading';
import Error from '../../components/Error';
import ProductCard from '../../components/ProductCard';

function Search() {
  const [searchParams] = useSearchParams();
  const query = searchParams.get("q");
  const url = "http://localhost:3000/tarifler?q=" + query;

  const {data:tarifler,isLoading,error} = useFetch(url);

  return (
     <div className="row mt-3">
      <h2>Aranan Kelime "{query}"</h2>
      <hr />
      <Loading loading={isLoading}/>
      <Error error={error} />
      {/* {
       console.log(tarifler)
      } */}
      {
        // console.log(tarifler)
        (tarifler && tarifler.legth === 0 ) ? <Error error={error} /> : tarifler?.map(tarif =>(<ProductCard key={tarif.id} tarif={tarif} /> ))

        // tarifler?.map(tarif =>(
        //   <ProductCard key={tarif.id} tarif={tarif} />
        // ))
      }
     </div>

  )
}

export default Search
