import { useEffect, useState } from "react";


const useFetch = (url) => {
    const [data ,setData] = useState(null);

    useEffect(() =>{
        
        const FetchData = async () =>{
            const res = await fetch(url);
            const data = await res.json();
            setData(data);
        }
        FetchData();
    },[url])

    return {data};

};

export default useFetch;
