import React from 'react'
import './ProductCard.css';
import { Link } from 'react-router-dom';
import { useContext } from 'react';
import { ThemeContext } from '../contexts/ThemeContext';

function ProductCard({tarif}) {
  const {color} = useContext(ThemeContext);
  return (
    <div className="col-3 mb-3">
            <div className="card">
              <img src={`img/${tarif.resim}`} alt={tarif.baslik} />
              <div class="card-body">
                <h5 class="card-title">{tarif.baslik}</h5>
                <p class="aciklama">{tarif.aciklama}</p>
                <Link to={`/tarifler/${tarif.id}`} className={`btn btn-${color}`}>tarifi incele</Link>
               </div>
            </div>
          </div>
  )
}

export default ProductCard
