import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import useFetch from '../../hooks/useFetch';

function Detail() {
    const { id } = useParams();
    const url = "http://localhost:3000/tarifler/" + id;
    const {data:tarif, isLoading} = useFetch(url);

  return (
    <div className='row mt-5'>
        { isLoading && <div className='alert alert-warning'>YÃ¼kleniyor...</div> }
        {
            tarif && (
                <>
                    <div className="col-4">
                        <img src={`/img/${tarif.resim}`} alt={tarif.baslik} className='img-fluid rounded' />
                    </div>
                    <div className="col-8">
                        <h5>{tarif.baslik}</h5>
                        <p>{tarif.aciklama}</p>
                        <ul>
                            {
                                tarif.malzemeler.map(malzeme =>(
                                    <li>{malzeme}</li>
                                ))
                            }
                        </ul>
                    </div>
                    <div className="col-12 mt-4">
                        <h3>Hazirlanisi</h3>
                        <p>{tarif.hazirlanisi}</p>
                    </div>
                </>
            )
        }
    </div>
  )
}

export default Detail;
